"use strict";

var vacanciContainers = document.querySelectorAll('.vacanci-container');
var tabBtns = document.querySelectorAll('.tab-btn');
tabBtns.forEach(function (button) {
  button.addEventListener('click', function () {
    tabBtns.forEach(function (button) {
      button.classList.remove('active');
    });
    button.classList.add('active');
  });
});
function toggleVacanci(n) {
  vacanciContainers.forEach(function (item) {
    var itemAttr = item.getAttribute('vacanci-data');
    if (n == Number(itemAttr)) {
      vacanciContainers.forEach(function (item) {
        item.classList.remove('active');
      });
      item.classList.add('active');
    }
  });
}
var header = document.querySelector('.header-wrapper');
window.addEventListener('scroll', function () {
  if (window.scrollY >= 10) {
    header.style.backgroundColor = '#fff';
  } else {
    header.style.background = 'linear-gradient(90deg, rgba(255, 255, 255, 0.30) 0%, rgba(255, 255, 255, 0.60) 100%)';
  }
});
